import { FC } from 'react'
import NextHead from 'next/head'

/**
 * Interface
 */

type StateProps = {
  title?: string
  description?: string
  image?: string
}

interface MetaProps {
  state: StateProps
}
const domain = process.env.DOMAIN_URL || ''

const defaultTitle = "Kiiiyo's IO"
const defaultDescription = "@Kiiiyo's Weblog です。"

export const Meta: FC<MetaProps> = props => {
  const {
    state: { title, description, image }
  } = props
  return (
    <NextHead>
      <meta charSet="UTF-8" />
      <title>{title ? `${title} - ${defaultTitle}` : defaultTitle}</title>
      <meta
        name="description"
        content={description ? description : defaultDescription}
      />
      <meta
        property="og:title"
        content={title ? `${title} - ${defaultTitle}` : defaultTitle}
      />
      <meta
        property="og:image"
        content={image ? image : `${domain}/assets/image/share.png`}
      />

      <meta name="twitter:card" content="summary_large_image" />
      <meta name="twitter:site" content="@kiiiyo" />

      <link
        rel="shortcut icon"
        href="data:image/x-icon;base64,"
      />
      <script
        async
        src={`https://www.googletagmanager.com/gtag/js?id=${process.env.GA_TRACKING_ID}`}
      />
      <script
        dangerouslySetInnerHTML={{
          __html: `
          window.dataLayer = window.dataLayer || [];
          function gtag(){dataLayer.push(arguments);}
          gtag('js', new Date());
          gtag('config', '${process.env.GA_TRACKING_ID}');
        `
        }}
      />
    </NextHead>
  )
}
